SELECT * FROM ( SELECT CONCAT('O2O', CASE chain_orders.orders_type WHEN 100 THEN '线上' WHEN 101 THEN '线下' WHEN 102 THEN '线下扫码' END ) AS '订单类型', chain_orders.orders_sn AS '订单编号', CASE chain_orders.orders_state WHEN 0 THEN '订单被取消' WHEN 10 THEN '生成新订单' WHEN 30 THEN '已支付' WHEN 40 THEN '订单完成' WHEN 50 THEN '已接单' WHEN 60 THEN '已呼叫配送' WHEN 70 THEN '待收货' WHEN 80 THEN '待评价' END AS '订单状态', chain.chain_name AS '归属门店名称', store.store_name AS '归属店铺名称', goods_common.common_id AS '商品id', goods_common.goods_name AS '商品名称', chain_orders.create_time AS '下单时间', chain_orders.orders_amount + chain_orders.points_money_amount + chain_orders.freight_amount AS '订单金额', chain_orders.predeposit_amount AS '实付现金', chain_orders.points_money_amount AS '美豆抵扣', store.store_name AS '店铺名称', chain_orders.receiver_message AS '买家留言', member.member_id AS '买家编号', member.mobile AS '买家手机号' FROM chain_orders LEFT JOIN chain_orders_goods ON chain_orders_goods.orders_id = chain_orders.orders_id LEFT JOIN goods_common ON goods_common.common_id = chain_orders_goods.common_id LEFT JOIN chain ON chain.chain_id = chain_orders.chain_id LEFT JOIN store ON store.store_id = chain.store_id LEFT JOIN member ON member.member_id = chain_orders.member_id WHERE 1=1
AND chain_orders.orders_type = #{订单类型}
AND chain_orders.orders_state = #{订单状态}
AND chain_orders.orders_sn LIKE CONCAT('%', #{订单编号}, '%')
AND goods_common.goods_name LIKE CONCAT('%', #{商品名称}, '%')
AND chain.chain_name LIKE CONCAT('%', #{门店名称}, '%')
AND store.store_name LIKE CONCAT('%', #{店铺名称}, '%')
AND chain_orders.create_time BETWEEN #{下单时间_start} AND #{下单时间_end}
AND member.member_id = #{买家编号}
AND member.mobile LIKE CONCAT('%', #{买家手机号}, '%')
UNION ALL
SELECT 'B2C' AS '订单类型', orders.orders_sn AS '订单编号', CASE orders.orders_state WHEN 0 THEN '订单被取消' WHEN 10 THEN '生成新订单' WHEN 30 THEN '已支付' WHEN 40 THEN '订单完成' WHEN 50 THEN '已接单' WHEN 60 THEN '已呼叫配送' WHEN 70 THEN '待收货' WHEN 80 THEN '待评价' END AS '订单状态', '' AS '归属门店名称', store.store_name AS '归属店铺名称', goods_common.common_id AS '商品id', goods_common.goods_name AS '商品名称', orders.create_time AS '下单时间', orders.orders_amount + orders.points_money_amount + orders.freight_amount AS '订单金额', orders.predeposit_amount AS '实付现金', orders.points_money_amount AS '美豆抵扣', store.store_name AS '店铺名称', orders.receiver_message AS '买家留言', member.member_id AS '买家编号', member.mobile AS '买家手机号' FROM orders LEFT JOIN orders_goods ON orders_goods.orders_id = orders.orders_id LEFT JOIN goods_common ON goods_common.common_id = orders_goods.common_id LEFT JOIN store ON store.store_id = orders.store_id LEFT JOIN member ON member.member_id = orders.member_id WHERE 1=1
AND 1=2
AND orders.orders_state = #{订单状态}
AND orders.orders_sn LIKE CONCAT('%', #{订单编号}, '%')
AND goods_common.goods_name LIKE CONCAT('%', #{商品名称}, '%')
AND chain.chain_name LIKE CONCAT('%', #{门店名称}, '%')
AND store.store_name LIKE CONCAT('%', #{店铺名称}, '%')
AND orders.create_time BETWEEN #{下单时间_start} AND #{下单时间_end}
AND member.member_id = #{买家编号}
AND member.mobile LIKE CONCAT('%', #{买家手机号}, '%')
) AS x WHERE (x.订单类型 = 'B2C' OR x.订单类型 = 'O2O线上')
ORDER BY ${sort} ${order}

SELECT
store.store_id AS '店铺编号',
store.store_name AS '店铺名称',
store.class_name AS '店铺类型',
store.seller_name AS '商家用户名',
store.store_zy AS '主营行业',
store.store_create_time AS '开店时间',
store.store_end_time AS '过期时间',
CASE store.state
	WHEN 0 THEN '开启'
	WHEN 1 THEN '关闭'
END AS '店铺状态',
level_marketing_distribution_store_site.store_sign_discount AS '分销比例',
CONCAT(store.bill_cycle,
	CASE store.bill_cycle_type
		WHEN 1 THEN '月'
		WHEN 2 THEN '天'
	END
) AS '结算周期'
FROM store
LEFT JOIN level_marketing_distribution_store_site ON level_marketing_distribution_store_site.store_id = store.store_id
WHERE 1=1

AND store.store_id = #{店铺编号}


AND store.store_name LIKE CONCAT('%', #{店铺名称}, '%')


AND store.class_name LIKE CONCAT('%', #{店铺类型}, '%')


AND store.seller_name LIKE CONCAT('%',  #{商家用户名}, '%')


AND store.store_zy LIKE CONCAT('%', #{主营行业}, '%')


AND store.store_create_time >= #{开店时间_start}


AND store.store_create_time <= #{开店时间_end}


AND store.store_end_time >= #{过期时间_start}


AND store.store_end_time <= #{过期时间_end}


AND store.state = #{店铺状态}


AND level_marketing_distribution_store_site.store_sign_discount = #{分销比例}


AND store.bill_cycle = #{结算周期_num}


AND store.bill_cycle_type = #{结算周期_unit}


ORDER BY ${sort} ${order}

SELECT * FROM ( SELECT chain_orders.orders_sn AS '订单编号', CASE chain_orders.orders_state WHEN 0 THEN '订单被取消' WHEN 10 THEN '生成新订单' WHEN 30 THEN '已支付' WHEN 40 THEN '订单完成' WHEN 50 THEN '已接单' WHEN 60 THEN '已呼叫配送' WHEN 70 THEN '待收货' WHEN 80 THEN '待评价' END AS '订单状态', CONCAT('O2O', CASE chain_orders.orders_type WHEN 100 THEN '线上' WHEN 101 THEN '线下' WHEN 102 THEN '线下扫码' END ) AS '订单类型', chain.chain_name AS '门店名称', store.store_name AS '店铺名称', goods_common.common_id AS '商品id', goods_common.goods_name AS '商品名称', chain_orders.create_time AS '下单时间', chain_orders.orders_amount + chain_orders.points_money_amount + chain_orders.freight_amount AS '订单金额', chain_orders.predeposit_amount AS '实付现金', chain_orders.points_money_amount AS '美豆抵扣', chain_orders.receiver_message AS '买家留言', member.member_id AS '买家编号', member.mobile AS '买家手机号' FROM chain_orders LEFT JOIN chain_orders_goods ON chain_orders_goods.orders_id = chain_orders.orders_id LEFT JOIN goods_common ON goods_common.common_id = chain_orders_goods.common_id LEFT JOIN chain ON chain.chain_id = chain_orders.chain_id LEFT JOIN store ON store.store_id = chain.store_id LEFT JOIN member ON member.member_id = chain_orders.member_id WHERE 1=1
AND chain_orders.orders_sn LIKE CONCAT('%', #{订单编号}, '%')
AND chain_orders.orders_state = #{订单状态}
AND chain.chain_name LIKE CONCAT('%', #{门店名称}, '%')
AND store.store_name LIKE CONCAT('%', #{店铺名称}, '%')
AND goods_common.common_id = #{商品id}
AND goods_common.goods_name LIKE CONCAT('%', #{商品名称}, '%')
AND chain_orders.orders_amount = #{实付现金}
AND chain_orders.receiver_message LIKE CONCAT('%', #{买家留言}, '%')
AND member.member_id = #{买家编号}
AND member.mobile LIKE CONCAT('%', #{买家手机号}, '%')
AND chain_orders.create_time >= #{下单时间_start}
AND chain_orders.create_time <= #{下单时间_end}
UNION ALL
SELECT 'B2C' AS '订单类型', orders.orders_sn AS '订单编号', CASE orders.orders_state WHEN 0 THEN '订单被取消' WHEN 10 THEN '生成新订单' WHEN 30 THEN '已支付' WHEN 40 THEN '订单完成' WHEN 50 THEN '已接单' WHEN 60 THEN '已呼叫配送' WHEN 70 THEN '待收货' WHEN 80 THEN '待评价' END AS '订单状态', '' AS '门店名称', store.store_name AS '店铺名称', goods_common.common_id AS '商品id', goods_common.goods_name AS '商品名称', orders.create_time AS '下单时间', orders.orders_amount + orders.points_money_amount + orders.freight_amount AS '订单金额', orders.predeposit_amount AS '实付现金', orders.points_money_amount AS '美豆抵扣', orders.receiver_message AS '买家留言', member.member_id AS '买家编号', member.mobile AS '买家手机号' FROM orders LEFT JOIN orders_goods ON orders_goods.orders_id = orders.orders_id LEFT JOIN goods_common ON goods_common.common_id = orders_goods.common_id LEFT JOIN store ON store.store_id = orders.store_id LEFT JOIN member ON member.member_id = orders.member_id WHERE 1=1
AND orders.orders_sn LIKE CONCAT('%', #{订单编号}, '%')
AND orders.orders_state = #{订单状态}
AND store.store_name LIKE CONCAT('%', #{店铺名称}, '%')
AND goods_common.common_id = #{商品id}
AND goods_common.goods_name LIKE CONCAT('%', #{商品名称}, '%')
AND orders.orders_amount = #{实付现金}
AND orders.receiver_message LIKE CONCAT('%', #{买家留言}, '%')
AND member.member_id = #{买家编号}
AND member.mobile LIKE CONCAT('%', #{买家手机号}, '%')
AND orders.create_time >= #{下单时间_start}
AND orders.create_time <= #{下单时间_end}
) AS x WHERE x.订单类型 IN ('B2C', 'O2O线上')
AND x.订单类型 = #{订单类型}
AND x.订单金额 = #{订单金额}
ORDER BY ${sort} ${order}

SELECT * FROM ( SELECT chain.chain_id AS '门店编号', chain.chain_name AS '门店名称', chain.clerk_name AS '店主名称', chain.class_name AS '门店类型', chain.address AS '门店地址', chain.chain_phone AS '联系电话', online_rate.store_sign_discount AS '线上分销', offline_rate.store_sign_discount AS '线下分销' FROM chain LEFT JOIN ( SELECT level_marketing_distribution_chain_site.chain_id, level_marketing_distribution_chain_site.store_sign_discount FROM level_marketing_distribution_chain_site WHERE level_marketing_distribution_chain_site.chain_type = 1 ) AS online_rate ON online_rate.chain_id = chain.chain_id LEFT JOIN ( SELECT level_marketing_distribution_chain_site.chain_id, level_marketing_distribution_chain_site.store_sign_discount FROM level_marketing_distribution_chain_site WHERE level_marketing_distribution_chain_site.chain_type = 2 ) AS offline_rate ON offline_rate.chain_id = chain.chain_id ) AS x WHERE 1=1
AND 门店编号 = #{门店编号}
AND 门店名称 LIKE CONCAT('%',#{门店名称},'%')
AND 店主名称 LIKE CONCAT('%',#{店主名称},'%')
AND 门店类型 LIKE CONCAT('%',#{门店类型},'%')
AND 门店地址 LIKE CONCAT('%',#{门店地址},'%')
AND 联系电话 LIKE CONCAT('%',#{联系电话},'%')
AND 线上分销 = #{线上分销}
AND 线下分销 = #{线下分销}
ORDER BY ${sort} ${order}

SELECT chain_orders.create_time AS '创建订单时间', -- seller.seller_name AS '商家名称', -- store.store_name AS '店铺名称', chain.chain_name AS '门店名称', member.member_name AS '会员名称', member.mobile AS '会员电话号码', CASE chain_orders.orders_type WHEN 100 THEN 'O2O线上' WHEN 101 THEN 'O2O线下' WHEN 102 THEN 'O2O线下扫码' END AS '订单类型', chain_orders.orders_sn AS '订单编号', CASE chain_orders.payment_code WHEN 'predeposit' THEN '收益' WHEN 'wxpay' THEN '微信' WHEN 'alipay' THEN '支付宝' WHEN 'yunpay' THEN '云闪付' WHEN 'kangfutong' THEN '康付通' WHEN 'pinganjiankangka' THEN '平安健康卡' WHEN 'offline' THEN '货到付款' WHEN 'chain' THEN '门店付款' END AS '支付方式', CASE chain_orders.orders_state WHEN 0 THEN '订单被取消' WHEN 10 THEN '生成新订单' WHEN 30 THEN '已支付' WHEN 40 THEN '订单完成' WHEN 50 THEN '已接单' WHEN 60 THEN '已呼叫配送' WHEN 70 THEN '待收货' WHEN 80 THEN '待评价' END AS '订单状态', (chain_orders.orders_amount+chain_orders.points_money_amount+chain_orders.coupon_amount) AS '订单总金额', FORMAT(chain_orders.orders_amount+chain_orders.points_money_amount+chain_orders.coupon_amount - (FLOOR((chain_orders.orders_amount+chain_orders.points_money_amount+chain_orders.coupon_amount) * 0.13) / 100), 2) AS '预期到账金额', chain_orders.orders_amount AS '现金支付金额', chain_orders.points_money_amount AS '美豆抵扣金额', chain_orders.coupon_amount AS '平台券抵扣金额', '暂时无法统计' AS '订单消费返美豆' FROM chain_orders LEFT JOIN chain ON chain.chain_id = chain_orders.chain_id LEFT JOIN store ON store.store_id = chain.store_id LEFT JOIN seller ON seller.seller_id = store.seller_id LEFT JOIN member ON member.member_id = chain_orders.member_id WHERE chain_orders.orders_type = 102 AND chain.chain_name != ''
AND chain_orders.create_time BETWEEN #{创建订单开始时间} AND #{创建订单结束时间}
AND member.member_name=#{会员名称}
AND chain.chain_name=#{门店名称}
AND member.mobile=#{会员电话号码}
AND chain_orders.orders_sn=#{订单编号}
AND chain_orders.orders_type=#{订单类型}
AND chain_orders.payment_code=#{支付方式}
AND chain_orders.orders_state=#{订单状态}
ORDER BY chain.chain_name, chain_orders.create_time

SELECT t.*,FLOOR(t.`现金支付金额`*l.alone_consumer_rate)/100 as '订单消费返美豆' from (SELECT chain.chain_id, chain.chain_name AS '门店名称', chain_orders.create_time AS '创建订单时间', CASE chain_orders.orders_type WHEN 100 THEN 'O2O线上' WHEN 101 THEN 'O2O线下' WHEN 102 THEN 'O2O线下扫码' END AS '订单类型', chain_orders.orders_sn AS '订单编号', CASE chain_orders.payment_code WHEN 'predeposit' THEN '收益' WHEN 'wxpay' THEN '微信' WHEN 'alipay' THEN '支付宝' WHEN 'yunpay' THEN '云闪付' WHEN 'kangfutong' THEN '康付通' WHEN 'pinganjiankangka' THEN '平安健康卡' WHEN 'offline' THEN '货到付款' WHEN 'chain' THEN '门店付款' END AS '支付方式', CASE chain_orders.orders_state WHEN 0 THEN '订单被取消' WHEN 10 THEN '生成新订单' WHEN 30 THEN '已支付' WHEN 40 THEN '订单完成' WHEN 50 THEN '已接单' WHEN 60 THEN '已呼叫配送' WHEN 70 THEN '待收货' WHEN 80 THEN '待评价' END AS '订单状态', (chain_orders.orders_amount+chain_orders.points_money_amount+chain_orders.coupon_amount) AS '订单总金额', FORMAT(chain_orders.orders_amount+chain_orders.points_money_amount+chain_orders.coupon_amount - (FLOOR((chain_orders.orders_amount+chain_orders.points_money_amount+chain_orders.coupon_amount) * 0.13) / 100), 2) AS '预期到账金额', chain_orders.orders_amount AS '现金支付金额', chain_orders.points_money_amount AS '美豆抵扣金额', chain_orders.coupon_amount AS '平台券抵扣金额' FROM chain_orders LEFT JOIN chain ON chain.chain_id = chain_orders.chain_id -- LEFT JOIN member WHERE chain_orders.orders_type = 102 -- and chain_orders.orders_sn=5180000001721001 and chain_orders.orders_state=40
AND chain_orders.create_time BETWEEN #{创建订单开始时间} AND #{创建订单结束时间}
ORDER BY chain_orders.create_time) as t left join (SELECT * from level_marketing_distribution_chain_site where chain_type=2) as l on t.chain_id=l.chain_id WHERE 1=1
AND t.chain_id=#{chain_id}
AND t.门店名称=#{门店名称}
AND t.订单编号=#{订单编号}
AND t.订单类型=#{订单类型}
AND t.支付方式=#{支付方式}
AND t.订单状态=#{订单状态}
ORDER BY t.chain_id DESC
